mapper.select:
      SELECT ss.record_id as recordId,ss.stuff_name as stuffName
(SELECT sj.job_name FROM salon.job as sj where sj.record_id = (select stuff.job_id from salon.stuff_job as stuff
where stuff.stuff_id= ss.record_id)) as jobName
 FROM salon.stuff as ss where ss.stuff_name like #{stuffName}
and ss.store_id in(select salon.record_id from salon.salon as salon where salon.parent_id = #{salonId});
